export default function Sidebar({ sidebarOpen, toggleSidebar }) {
  return (
    <Wrapper sidebarOpen={sidebarOpen}>
      <SidebarHeader> 
        <BackBtn onClick={() => toggleSidebar(!sidebarOpen)}>
          <BackArrow />
          <Heading>Menu</Heading>
        </BackBtn>
      </SidebarHeader>

      <UlStyle>
        <li>
          <StyledLink to="/" onClick={() => toggleSidebar(!sidebarOpen)}>
            {icons.home}
            Home
          </StyledLink>
        </li>
        <li>
          <StyledLink to="/services" onClick={() => toggleSidebar(!sidebarOpen)}>
            {icons.services}
            Services
          </StyledLink>
        </li>
        <li>
          <StyledLink to="/projects" onClick={() => toggleSidebar(!sidebarOpen)}>
            {icons.projects}
            Projects
          </StyledLink>
        </li>
        <li>
          <StyledLink to="/pricing" onClick={() => toggleSidebar(!sidebarOpen)}>
            {icons.pricing}
            Pricing
          </StyledLink>
        </li>
        <li>
          <StyledLink to="/contact" onClick={() => toggleSidebar(!sidebarOpen)}>
            {icons.contact}
            Contact
          </StyledLink>
        </li>
      </UlStyle>
    </Wrapper>
  );
}

// Sidebar container
const Wrapper = styled.nav`
  width: 300px;
  height: 100vh;
  position: fixed;
  top: 0;
  left: ${(props) => (props.sidebarOpen ? "0" : "-300px")};
  transition: left 0.3s ease-in-out;
  z-index: 1000;
  background: linear-gradient(180deg, #001d3d 0%, #000b1f 100%);
  color: #f0f0f0;
  box-shadow: 2px 0 15px rgba(0, 0, 0, 0.7);
  border-radius: 0 10px 10px 0;

  @media (max-width: 768px) {
    width: 60%;
    left: ${(props) => (props.sidebarOpen ? "0" : "-100%")};
  }
`;

// Sidebar header
const SidebarHeader = styled.div`
  padding: 20px;
  border-bottom: 1px solid rgba(255, 255, 255, 0.2);
  display: flex;
  align-items: center;
`;

// Back button for the sidebar
const BackBtn = styled.button`
  border: none;
  background-color: transparent;
  cursor: pointer;
  display: flex;
  align-items: center;
  color: #ffffff;
  padding: 10px;
  font-size: 1.4rem;
  transition: background-color 0.3s, color 0.3s;

  &:hover {
    background-color: rgba(255, 255, 255, 0.1);
    border-radius: 5px;
    color: #ffcc00;
  }

  svg {
    fill: #ffffff;
    width: 24px;
    height: 24px;
    margin-right: 10px;
    transition: fill 0.3s;
  }

  &:hover svg {
    fill: #ffcc00;
  }
`;

// Unordered list for sidebar links
const UlStyle = styled.ul`
  list-style: none;
  padding: 40px 20px;
  margin: 0;
`;

// Styled link for sidebar navigation
const StyledLink = styled(Link)`
  color: #f0f0f0;
  font-size: 1.3rem;
  padding: 15px 20px;
  display: flex; /* Use flex for icon and text alignment */
  align-items: center; /* Center the icon and text vertically */
  border-radius: 5px;
  transition: background-color 0.3s, color 0.3s, transform 0.3s;
  text-decoration: none;
  position: relative;

  &:hover {
    background-color: rgba(255, 255, 255, 0.1);
    color: #ffcc00;
    transform: translateY(-2px);
  }

  &.active {
    background-color: #ffcc00;
    color: #121212;
  }

  svg {
    margin-right: 10px; /* Space between icon and text */
    width: 20px; /* Adjust icon size */
    height: 20px; /* Adjust icon size */
    fill: currentColor; /* Make icon color match text color */
  }
`;

// Adding 3D effect to headings
const Heading = styled.span`
  font-weight: bold;
  color: #90e0ef;
  text-shadow: 1px 1px 3px rgba(0, 0, 0, 0.5), 0 0 25px rgba(0, 0, 0, 0.5);
  margin-left: 5px;
`;
